//import { GET_CHARACTERS } from "../types/index.js";

const initialState = {
    characters: [],
    allCharacters: []
};

export default function rootReducer(state = initialState, action) {
    switch(action.type){
        case "GET_CHARACTERS":
            return{
                ...state,
                characters: action.payload,
                allCharacters: action.payload,
        }
        case "FILTER_CREATED":
            const allCharacters2 = state.allCharacters
            const createdFilter= action.payload === "created" ? allCharacters2.filter(el => el.created) : allCharacters2.filter(el => !el.created)
            return{
                ...state,
                characters: action.payload === "All" ? state.allCharacters : createdFilter
            }
        default: 
        return state 
    }
  
}
