//import { GET_CHARACTERS } from "../types/index.js";

const initialState = {
    characters: [],
    allCharacters: []
};

export default function rootReducer(state = initialState, action) {
    switch(action.type){
        case "GET_CHARACTERS":
            return{
                ...state,
                characters: action.payload,
                allCharacters: action.payload,
        }
        default: 
        return state 
    }
  
}
