import React from "react";
import {useState, useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import { getCharacters } from "../actions"; // importo las acciones
import {Link} from "react-router-dom"; 
import Card from "./Card";

export default function Home(){

    const dispatch= useDispatch()
    
    const allCharacters= useSelector((state) => state.characters) // ESTADO GLOBAL 
    
    useEffect(()=>{
        dispatch(getCharacters())
    },[dispatch])
    
    // ||||| lo que hace es volver a cargar todos los personajes ||||
    function handleClick(e){
    e.preventDefault();
    dispatch(getCharacters())
    }
    
    // |||| sirve para cambiar el value dependiendo donde clickee el usuario |||
    //     function handlefilterStatus(e){
    //     dispatch(filterbystatus(e.target.value))
    //     }
    // ||| sirve para averiguar los creados |||
    //     function handlefilterCreated(e){
    //     dispatch(filterCreated(e.target.value))
    //     }
    
    
    return(
        <div>
            <Link to= "/create">CREAR PERSONAJE</Link>
            <h1>MI PAGINA WEB</h1>

            <button onClick={e => {handleClick(e)}}>VOLVER A CARGAR</button> 
       
            <select>
            {/* onChange={e => handlefilterstatus(e)} */}
                <option value="asc" >Ascendente</option>
                <option value="desc">Descendente</option>
            </select>
            <select>
                <option value="All" >Todos</option>
                <option value="created">Creados</option>
                <option value="api">Existentes</option>
            </select>
        { allCharacters?.map( (el) =>{
            return
            <Card name={el.name} species= {el.species} origen= {el.origin} image= {el.image} />
        })
        } 
        </div>
    
       )
    
    
    }
